(function(){tinymce.PluginManager.requireLangPack('linktofile');tinymce.create('tinymce.plugins.LFPlugin',{init:function(ed,url){ed.addCommand('mceLF',function(){ed.windowManager.open({file:'modlinkfile.php?blog_id='+blog_id,width:450,height:500,inline:1},{plugin_url:url,})});ed.addButton('linktofile',{title:'linktofile.desc',cmd:'mceLF',image:url+'/img/example.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('linktofile',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('linktofile',tinymce.plugins.LFPlugin)})();
