(function(){tinymce.PluginManager.requireLangPack('linktopost');tinymce.create('tinymce.plugins.LPPlugin',{init:function(ed,url){ed.addCommand('mceLP',function(){ed.windowManager.open({file:'linkblog.php?blog_id='+blog_id,width:450,height:500,inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('linktopost',{title:'linktopost.desc',cmd:'mceLP',image:url+'/img/link.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('linktopost',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Link to post',author:'Mike',authorurl:'',infourl:'',version:"1.0"}}});tinymce.PluginManager.add('linktopost',tinymce.plugins.LPPlugin)})();
