<?phpdefine('FPDF_FONTPATH','font/');require('fpdf.php');class PDF extends FPDF{//Page headerfunction Header(){	$this->Image('img/logo.jpg', 5, 5, 25);	}//Page footerfunction Footer(){	$this->SetFont('Arial','',8);	$text = $GLOBALS['cinfo']['name'].", ".$GLOBALS['cinfo']['address1'].", ";	$this->Text(5,101,$text);	$text = $GLOBALS['cinfo']['address2'].", ".$GLOBALS['cinfo']['address3'].", ".$GLOBALS['cinfo']['town'].", ".$GLOBALS['cinfo']['postcode'].".";	$this->Text(5,103.5,$text);	$text = "Phone: ".$GLOBALS['cinfo']['phone']." Email: ".$GLOBALS['cinfo']['email'].", Web: ".$GLOBALS['cinfo']['web'];	$this->Text(5,106,$text);}function printthispage($orderid){	$this->AddPage();	$this->SetFont('Arial','B',12);	$text = "Student ID:";	$this->Text(40,20,$text);	}}//start output$pdf=new PDF('L','mm','dl');$pdf->printthispage($_REQUEST[id]);$pdf->Output();?>